import * as React from 'react';
import './article.scss';

type ArticleProps = any;

const Article: React.FC<ArticleProps> = ({ item }) => {
  return (
    <li className="">
      <div className="max-w-[960px] mx-auto">
        <img
          className="object-cover	object-top rounded-md	h-[450px] w-full"
          src={require(`../../../../assets/img/${item.img}`)}
          alt=""
        />
      </div>
      {/* border-l-2 py-8 pl-14 border-l-gray-400 border-opacity-50 border-solid */}
      <article className="before:content[''] before:w-1 before:h-full before:absolute before:bg-slate-500 flex flex-col items-center max-w-[570px] relative justify-center mx-auto ">
        <p className="absolute -left-2 py-3 bg-white top-8 text-red-700 font-bold text-lg whitespace-pre-wrap w-3 flex flex-col items-center justify-center">
          {item.date.split(' ').map((dateItem: string) => (
            <span className="leading-5">{dateItem}</span>
          ))}
        </p>
        <p className="text-4xl font-black pb-8">{item.title}</p>
        <p className="text-base leading-5">{item.description}</p>
      </article>
    </li>
  );
};

export { Article };
