import * as React from 'react';
import './numbers.scss';

const Numbers: React.FC = () => {
  const [number, setNumber] = React.useState(1);

  React.useEffect(() => {
    const count = setTimeout(() => {
      if (number !== 17) {
        setNumber((value) => {
          return value + 1;
        });
      }
      console.log('work');
    }, 100);

    return () => {
      clearInterval(count);
    };
  }, [number]);

  return <div className={'numbers-wrapper'}>{number}</div>;
};

export { Numbers };
