import * as React from 'react';
import './numbers.scss';

const Numbers: React.FC = () => {
  const [number, setNumber] = React.useState(1);

  React.useEffect(() => {
    setTimeout(() => {
      const count = setTimeout(() => {
        if (number !== 17) {
          setNumber((value) => {
            return value + 1;
          });
        }
      }, 1500 / number);

      return () => {
        clearInterval(count);
      };
    }, 7000);
  }, [number]);

  return (
    <div className="numbers flex justify-center  align-bottom items-end pt-16">
      <img
        // className="w-screen"
        src={require(`../../assets/img/${number}.png`)}
        alt={String(number)}
      />
      {/* <p className="relative font-black  tracking-[1rem] text-[96px] ">
        <span
          style={{ color: 'red' }}
          className="absolute -bottom-6  tracking-[1rem] -left-[55%] font-black text-[250px] opacity-50 "
        >
          {number < 10 ? `0${number}` : number}
        </span>
        {number < 10 ? `0${number}` : number}
      </p> */}
    </div>
  );
};

export { Numbers };
