import * as React from 'react';
import { Tabs } from '../Tabs';
import './dropdown.scss';

type Submenu = {
  title: string;
  submenu?: Submenu;
}[];

type DropDownProps = {
  submenus: Submenu;
  dropdown: boolean;
  depthLevel: number;
};

const Dropdown: React.FC<DropDownProps> = ({
  submenus,
  dropdown,
  depthLevel,
}) => {
  const depthLevelLocal = depthLevel + 1;
  const dropdownClass = depthLevel > 1 ? 'dropdown-submenu' : '';

  return (
    <ul
      className={`dropdown ${dropdownClass} ${dropdown ? 'dropdown-show' : ''}`}
    >
      {submenus.map((submenu: any, index: any) => (
        <Tabs
          items={submenu}
          key={String(index + Date.now())}
          depthLevel={depthLevelLocal}
        />
      ))}
    </ul>
  );
};

export { Dropdown };
