import * as React from 'react';
import './numbers.scss';

const Numbers: React.FC = () => {
  const [number, setNumber] = React.useState(1);

  React.useEffect(() => {
    const count = setTimeout(() => {
      if (number !== 17) {
        setNumber((value) => {
          return value + 1;
        });
      }
    }, 1500 / number);

    return () => {
      clearInterval(count);
    };
  }, [number]);

  return (
    <div className="flex justify-center align-bottom items-end pt-16">
      <p className="relative font-extrabold text-[110px] ">
        <span className="number absolute -bottom-6 -left-1/2 font-extrabold text-[250px] opacity-50 ">
          {number < 10 ? `0${number}` : number}
        </span>
        {number < 10 ? `0${number}` : number}
      </p>
    </div>
  );
};

export { Numbers };
