import * as React from 'react';
import { Tabs } from '../Tabs';
import './dropdown.scss';

type Submenu = {
  title: string;
  submenus?: Submenu;
}[];

type DropDownProps = {
  // submenus: {
  //   title: string;
  // }[];
  submenus: Submenu;
  dropdown: boolean;
};

const Dropdown: React.FC<DropDownProps> = ({ submenus, dropdown }) => {
  return (
    <ul className={`dropdown ${dropdown ? 'dropdown-show' : ''}`}>
      {submenus.map((item) => (
        <div className="title" key={item.title}>
          {item.submenus && <Tabs titles={item.submenus} key={item.title} />}
        </div>
      ))}
    </ul>
  );
};

