import * as React from 'react';
import { useState } from 'react';
import { Dropdown } from '../Dropdown';
import './tabs.scss';

type Submenu = {
  title: string;
  submenu?: Submenu;
}[];

type TabsProps = {
  titles: {
    title: string;
    // submenu?: {
    //   title: string;
    // }[];
    submenu?: Submenu;
  };
};

const Tabs: React.FC<any> = ({ titles }) => {
  const [dropdown, setDropdown] = useState(false);

  const handleDropdown = () => {
    setDropdown((value) => !value);
  };
  return (
    <li className={'tabs-wrapper'}>
      {titles.submenu ? (
        <>
          <button
            onClick={handleDropdown}
            aria-expanded={dropdown}
            type="button"
            aria-haspopup="menu"
          >
            {titles.title}{' '}
          </button>
          <Dropdown dropdown={dropdown} submenus={titles.submenu} />
        </>
      ) : (
        <a href="/#">{titles.title}</a>
      )}
    </li>
  );
};

export { Tabs };
