import * as React from 'react';
import { Tabs } from '../Tabs';
import './dropdown.scss';

type Submenu = {
  title: string;
  submenus?: Submenu;
}[];

type DropDownProps = {
  // submenus: {
  //   title: string;
  // }[];
  submenus: Submenu;
  dropdown: boolean;
};

const Dropdown: React.FC<DropDownProps> = ({ submenus, dropdown }) => {
  return (
    <ul className={`dropdown ${dropdown ? 'dropdown-show' : ''}`}>
      {submenus.map(({ title, submenus }) => (
        // <li className="item" key={title}>
        //   <a href="/#">{title}</a>
        // </li>
        <Tabs titles={submenus} key={title} />
      ))}
    </ul>
  );
};

export { Dropdown };
