import * as React from 'react';
import { Dropdown } from '../Dropdown';
import './tabs.scss';

type TitlesProps = {
  titles: {
    title: string;
    submenu?: {
      title: string;
    }[];
  }[];
};

const Tabs: React.FC<TitlesProps> = ({ titles }) => {
  return (
    <div className={'tabs-wrapper'}>
      <ul className="tabs">
        {titles.map(({ title, submenu }) => (
          <li key={title} className="tab">
            {submenu ? (
              <>
                <button type="button" aria-haspopup="menu">
                  {title}
                </button>
                <Dropdown submenus={submenu} />
              </>
            ) : (
              <a href="/#">{title}</a>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export { Tabs };
