import * as React from 'react';
import { Tabs } from '../Tabs';
import './dropdown.scss';

type Submenu = {
  title: string;
  submenu?: Submenu;
}[];

type DropDownProps = {
  // submenus: {
  //   title: string;
  // }[];
  submenu: Submenu;
  dropdown: boolean;
};

const Dropdown: React.FC<DropDownProps> = ({ submenus, dropdown }) => {
  return (
    <ul className={`dropdown ${dropdown ? 'dropdown-show' : ''}`}>
      {submenus.map(
        (item) =>
          item.submenu && <Tabs titles={item.submenus} key={item.title} />
      )}
    </ul>
  );
};

