import axios from 'axios';
import React, { useEffect, useState } from 'react';
import './pagination.scss';

type Photo = {
  albumId: number;
  id: number;
  title: string;
  url: string;
  thumbnailUrl: string;
};

const Pagination: React.FC = () => {
  const [photos, setPhotos] = useState<Photo[]>([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [fetching, setFetching] = useState(true);
  const [totalPhotosCount, setTotalPhotosCount] = useState(100);

  useEffect(() => {
    if (fetching) {
      axios
        .get(
          `https://jsonplaceholder.typicode.com/photos?_limit=10&_page=${currentPage}`
        )
        .then((response) => {
          setPhotos([...photos, ...response.data]);
          setTotalPhotosCount(Number(response.headers['x-total-count']));
          setCurrentPage((value) => value + 1);
        })
        .finally(() => {
          setFetching(false);
        });
    }
  }, [fetching]);

  const scrollHandler = (event: any): void => {
    if (
      event.target.documentElement.scrollHeight -
        (event.target.documentElement.scrollTop + window.innerHeight) <
        100 &&
      photos.length < totalPhotosCount
    ) {
      setFetching(true);
    }
  };

  useEffect(() => {
    document.addEventListener('scroll', scrollHandler);
    return () => {
      document.removeEventListener('scroll', scrollHandler);
    };
  }, []);

  return (
    <div className={'pagination-wrapper'}>
      {photos.map((photo) => (
        <div key={photo.id}>
          <p>
            {photo.id} {photo.title}
          </p>
          <img src={photo.thumbnailUrl} alt={photo.title} />
        </div>
      ))}
      {fetching && <div className="loader">loading...</div>}
    </div>
  );
};

export { Pagination };
