import axios from 'axios';
import React, { useEffect, useState } from 'react';
import './pagination.scss';

type Photo = {
  albumId: 1;
  id: 2;
  title: 'reprehenderit est deserunt velit ipsam';
  url: 'https://via.placeholder.com/600/771796';
  thumbnailUrl: 'https://via.placeholder.com/150/771796';
};

const Pagination: React.FC = () => {
  const [photos, setPhotos] = useState<Photo[]>([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [fetching, setFetching] = useState(true);
  const [totalPageCount, setTotalPageCount] = useState(0);

  useEffect(() => {
    if (fetching) {
      axios
        .get(
          `https://jsonplaceholder.typicode.com/photos?_limit=10&_page=${currentPage}`
        )
        .then((response) => {
          setPhotos([...photos, ...response.data]);
          setCurrentPage((value) => value + 1);
          // @ts-ignore
          setTotalPageCount(response.headers['x-total-count']);
        })
        .finally(() => setFetching(false));
    }
  }, [fetching]);

  const scrollHandler = (event: any) => {
    if (
      event.target.documentElement.scrollHeight -
        (event.target.documentElement.scrollTop + window.innerHeight) <
      100
    ) {
      setFetching(true);
    }
    console.log(event.target.documentElement.scrollHeight);
    console.log(event.target.documentElement.scrollTop);
    console.log(window.innerHeight);
  };

  useEffect(() => {
    document.addEventListener('scroll', scrollHandler);
    return () => {
      document.removeEventListener('scroll', scrollHandler);
    };
  }, []);

  return (
    <div className={'pagination-wrapper'}>
      {photos.map((photo) => (
        <div>
          <p>
            {photo.id} {photo.title}
          </p>
          <img src={photo.thumbnailUrl} alt={photo.title} />
        </div>
      ))}
      {fetching && <div>loading...</div>}
    </div>
  );
};

export { Pagination };
