import * as React from 'react';
import './article.scss';

type ArticleProps = any;

const Article: React.FC<ArticleProps> = ({ item }) => {
  return (
    <li className={'article-wrapper'}>
      <div className="max-w-[960px] mx-auto mb-8">
        <img src={require(`../../../../assets/img/${item.img}`)} alt="" />
      </div>
      <article className="flex flex-col items-center max-w-[570px] relative justify-center mx-auto">
        <span className="absolute left-0 top-0 text-red-700 font-bold text-lg whitespace-pre-wrap w-3 text-center">
          {item.date}
        </span>
        <p className="text-4xl font-black ">{item.title}</p>
        <p>{item.description}</p>
      </article>
    </li>
  );
};

export { Article };
