import * as React from 'react';
import { useState } from 'react';
import { Dropdown } from '../Dropdown';
import './tabs.scss';

type Submenu = {
  title: string;
  submenus?: Submenu;
}[];

type TitlesProps = {
  titles: {
    title: string;
    submenu?: Submenu;
  }[];
};

const Tabs: React.FC<TitlesProps> = ({ titles }) => {
  const [dropdown, setDropdown] = useState(false);

  const handleDropdown = () => {
    setDropdown((value) => !value);
  };
  return (
    <div className={'tabs-wrapper'}>
      <ul className="tabs">
        {titles.map(({ title, submenu }) => (
          <li key={title} className="tab">
            {submenu ? (
              <>
                <button
                  aria-expanded={dropdown}
                  type="button"
                  aria-haspopup="menu"
                  onClick={handleDropdown}
                >
                  {title}
                </button>
                <Dropdown submenus={submenu} dropdown={dropdown} />
              </>
            ) : (
              <a href="/#">{title}</a>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export { Tabs };
