import * as React from 'react';
import './numbers.scss';

const Numbers: React.FC = () => {
  const [number, setNumber] = React.useState(1);

  React.useEffect(() => {
    const count = setTimeout(() => {
      setNumber((value) => value + 1);
      if (number === 17) {
        return () => {
          clearInterval(count);
        };
      }
    }, 100);
  }, [number]);

  return <div className={'numbers-wrapper'}>{number}</div>;
};

export { Numbers };
